mapper.delete:
delete from crm.tbl_activity
where id in

    #{aid}

mapper.select:
select count(*) from crm.tbl_activity as a join crm.tbl_user as u on a.owner = u.id
a.name like '%' #{name} '%'
and u.name like '%' #{owner} '%'
and a.starDate > #{startDate}
and a.endDate < #{endDate}

select a.id, a.name, u.name as owner, a.startDate, a.endDate from crm.tbl_activity as a join crm.tbl_user as u on a.owner = u.id
a.name like '%' #{name} '%'
and u.name like '%' #{owner} '%'
and a.starDate > #{startDate}
and a.endDate < #{endDate}
order by a.createTime desc limit #{skipCount},#{pageSize};
